import React, { useState, useEffect, useCallback } from 'react';
import {
  View,
  Text,
  StyleSheet,
  FlatList,
  TextInput,
  TouchableOpacity,
  Modal,
  Alert,
  ActivityIndicator,
} from 'react-native';
import { SafeAreaProvider, SafeAreaView } from 'react-native-safe-area-context';
import { StatusBar } from 'expo-status-bar';
import { Ionicons } from '@expo/vector-icons';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { v4 as uuidv4 } from 'uuid';
import { theme } from './theme';

type Item = {
  id: string;
  title: string;
  description: string;
  completed: boolean;
  createdAt: string;
};

const STORAGE_KEY = '@crud_items';

export default function App() {
  const [items, setItems] = useState<Item[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [modalVisible, setModalVisible] = useState(false);
  const [editingItem, setEditingItem] = useState<Item | null>(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  // Load items from storage
  useEffect(() => {
    loadItems();
  }, []);

  const loadItems = async () => {
    try {
      const stored = await AsyncStorage.getItem(STORAGE_KEY);
      if (stored) {
        setItems(JSON.parse(stored));
      }
    } catch (error) {
      console.error('Error loading items:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const saveItems = async (newItems: Item[]) => {
    try {
      await AsyncStorage.setItem(STORAGE_KEY, JSON.stringify(newItems));
      setItems(newItems);
    } catch (error) {
      console.error('Error saving items:', error);
    }
  };

  const handleAddItem = () => {
    if (!title.trim()) {
      Alert.alert('Fehler', 'Bitte gib einen Titel ein.');
      return;
    }

    if (editingItem) {
      // Update existing item
      const updated = items.map(item =>
        item.id === editingItem.id
          ? { ...item, title: title.trim(), description: description.trim() }
          : item
      );
      saveItems(updated);
    } else {
      // Add new item
      const newItem: Item = {
        id: uuidv4(),
        title: title.trim(),
        description: description.trim(),
        completed: false,
        createdAt: new Date().toISOString(),
      };
      saveItems([newItem, ...items]);
    }

    closeModal();
  };

  const handleDeleteItem = (id: string) => {
    Alert.alert(
      'L√∂schen best√§tigen',
      'M√∂chtest du diesen Eintrag wirklich l√∂schen?',
      [
        { text: 'Abbrechen', style: 'cancel' },
        {
          text: 'L√∂schen',
          style: 'destructive',
          onPress: () => {
            const filtered = items.filter(item => item.id !== id);
            saveItems(filtered);
          },
        },
      ]
    );
  };

  const toggleComplete = (id: string) => {
    const updated = items.map(item =>
      item.id === id ? { ...item, completed: !item.completed } : item
    );
    saveItems(updated);
  };

  const openEditModal = (item: Item) => {
    setEditingItem(item);
    setTitle(item.title);
    setDescription(item.description);
    setModalVisible(true);
  };

  const openAddModal = () => {
    setEditingItem(null);
    setTitle('');
    setDescription('');
    setModalVisible(true);
  };

  const closeModal = () => {
    setModalVisible(false);
    setEditingItem(null);
    setTitle('');
    setDescription('');
  };

  const renderItem = ({ item }: { item: Item }) => (
    <View style={[styles.itemCard, item.completed && styles.itemCompleted]}>
      <TouchableOpacity
        style={styles.checkbox}
        onPress={() => toggleComplete(item.id)}
      >
        <Ionicons
          name={item.completed ? 'checkbox' : 'square-outline'}
          size={24}
          color={item.completed ? theme.palette.success : theme.palette.text.secondary}
        />
      </TouchableOpacity>

      <View style={styles.itemContent}>
        <Text style={[styles.itemTitle, item.completed && styles.itemTitleCompleted]}>
          {item.title}
        </Text>
        {item.description ? (
          <Text style={styles.itemDescription}>{item.description}</Text>
        ) : null}
        <Text style={styles.itemDate}>
          {new Date(item.createdAt).toLocaleDateString('de-DE')}
        </Text>
      </View>

      <View style={styles.itemActions}>
        <TouchableOpacity
          style={styles.actionButton}
          onPress={() => openEditModal(item)}
        >
          <Ionicons name="create-outline" size={20} color={theme.palette.primary} />
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.actionButton}
          onPress={() => handleDeleteItem(item.id)}
        >
          <Ionicons name="trash-outline" size={20} color={theme.palette.error} />
        </TouchableOpacity>
      </View>
    </View>
  );

  if (isLoading) {
    return (
      <View style={[styles.container, styles.centered]}>
        <ActivityIndicator size="large" color={theme.palette.primary} />
      </View>
    );
  }

  return (
    <SafeAreaProvider>
      <SafeAreaView style={styles.container} edges={['top']}>
        <StatusBar style="light" />

        <View style={styles.header}>
          <Text style={styles.headerTitle}>Meine Eintr√§ge</Text>
          <Text style={styles.headerSubtitle}>
            {items.filter(i => !i.completed).length} offen
          </Text>
        </View>

        <FlatList
          data={items}
          renderItem={renderItem}
          keyExtractor={item => item.id}
          contentContainerStyle={styles.listContent}
          ListEmptyComponent={
            <View style={styles.emptyState}>
              <Ionicons name="folder-open" size={64} color={theme.palette.text.secondary} />
              <Text style={styles.emptyText}>Keine Eintr√§ge vorhanden</Text>
              <Text style={styles.emptySubtext}>Tippe auf + um einen neuen Eintrag zu erstellen</Text>
            </View>
          }
        />

        <TouchableOpacity style={styles.fab} onPress={openAddModal}>
          <Ionicons name="add" size={28} color={theme.palette.background} />
        </TouchableOpacity>

        {/* Add/Edit Modal */}
        <Modal
          visible={modalVisible}
          animationType="slide"
          transparent={true}
          onRequestClose={closeModal}
        >
          <View style={styles.modalOverlay}>
            <View style={styles.modalContent}>
              <View style={styles.modalHeader}>
                <Text style={styles.modalTitle}>
                  {editingItem ? 'Eintrag bearbeiten' : 'Neuer Eintrag'}
                </Text>
                <TouchableOpacity onPress={closeModal}>
                  <Ionicons name="close" size={24} color={theme.palette.text.secondary} />
                </TouchableOpacity>
              </View>

              <TextInput
                style={styles.input}
                placeholder="Titel"
                placeholderTextColor={theme.palette.text.secondary}
                value={title}
                onChangeText={setTitle}
                autoFocus
              />

              <TextInput
                style={[styles.input, styles.textArea]}
                placeholder="Beschreibung (optional)"
                placeholderTextColor={theme.palette.text.secondary}
                value={description}
                onChangeText={setDescription}
                multiline
                numberOfLines={4}
              />

              <TouchableOpacity style={styles.submitButton} onPress={handleAddItem}>
                <Text style={styles.submitButtonText}>
                  {editingItem ? 'Speichern' : 'Hinzuf√ºgen'}
                </Text>
              </TouchableOpacity>
            </View>
          </View>
        </Modal>
      </SafeAreaView>
    </SafeAreaProvider>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: theme.palette.background,
  },
  centered: {
    justifyContent: 'center',
    alignItems: 'center',
  },
  header: {
    padding: theme.spacing.lg,
    borderBottomWidth: 1,
    borderBottomColor: theme.palette.border,
  },
  headerTitle: {
    fontSize: 28,
    fontWeight: '700',
    color: theme.palette.text.primary,
  },
  headerSubtitle: {
    fontSize: 14,
    color: theme.palette.text.secondary,
    marginTop: theme.spacing.xs,
  },
  listContent: {
    padding: theme.spacing.md,
    paddingBottom: 100,
  },
  itemCard: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: theme.palette.card,
    borderRadius: theme.borderRadius.lg,
    padding: theme.spacing.md,
    marginBottom: theme.spacing.md,
    borderWidth: 1,
    borderColor: theme.palette.border,
  },
  itemCompleted: {
    opacity: 0.6,
  },
  checkbox: {
    marginRight: theme.spacing.md,
  },
  itemContent: {
    flex: 1,
  },
  itemTitle: {
    fontSize: 16,
    fontWeight: '600',
    color: theme.palette.text.primary,
  },
  itemTitleCompleted: {
    textDecorationLine: 'line-through',
    color: theme.palette.text.secondary,
  },
  itemDescription: {
    fontSize: 14,
    color: theme.palette.text.secondary,
    marginTop: theme.spacing.xs,
  },
  itemDate: {
    fontSize: 12,
    color: theme.palette.text.secondary,
    marginTop: theme.spacing.xs,
  },
  itemActions: {
    flexDirection: 'row',
    gap: theme.spacing.sm,
  },
  actionButton: {
    padding: theme.spacing.sm,
  },
  emptyState: {
    alignItems: 'center',
    paddingVertical: theme.spacing.xl * 2,
  },
  emptyText: {
    fontSize: 18,
    fontWeight: '600',
    color: theme.palette.text.primary,
    marginTop: theme.spacing.md,
  },
  emptySubtext: {
    fontSize: 14,
    color: theme.palette.text.secondary,
    marginTop: theme.spacing.xs,
  },
  fab: {
    position: 'absolute',
    bottom: 24,
    right: 24,
    width: 56,
    height: 56,
    borderRadius: 28,
    backgroundColor: theme.palette.primary,
    alignItems: 'center',
    justifyContent: 'center',
    elevation: 4,
    shadowColor: theme.palette.primary,
    shadowOffset: { width: 0, height: 4 },
    shadowOpacity: 0.3,
    shadowRadius: 8,
  },
  modalOverlay: {
    flex: 1,
    backgroundColor: 'rgba(0, 0, 0, 0.7)',
    justifyContent: 'flex-end',
  },
  modalContent: {
    backgroundColor: theme.palette.card,
    borderTopLeftRadius: theme.borderRadius.lg,
    borderTopRightRadius: theme.borderRadius.lg,
    padding: theme.spacing.lg,
  },
  modalHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: theme.spacing.lg,
  },
  modalTitle: {
    fontSize: 20,
    fontWeight: '700',
    color: theme.palette.text.primary,
  },
  input: {
    backgroundColor: theme.palette.background,
    borderRadius: theme.borderRadius.md,
    padding: theme.spacing.md,
    fontSize: 16,
    color: theme.palette.text.primary,
    borderWidth: 1,
    borderColor: theme.palette.border,
    marginBottom: theme.spacing.md,
  },
  textArea: {
    height: 100,
    textAlignVertical: 'top',
  },
  submitButton: {
    backgroundColor: theme.palette.primary,
    borderRadius: theme.borderRadius.md,
    padding: theme.spacing.md,
    alignItems: 'center',
    marginTop: theme.spacing.sm,
  },
  submitButtonText: {
    fontSize: 16,
    fontWeight: '600',
    color: theme.palette.background,
  },
});

export const theme = {
  palette: {
    background: '#020617',
    card: '#0f172a',
    primary: '#22d3ee',
    accent: '#f472b6',
    border: '#1e293b',
    text: {
      primary: '#f8fafc',
      secondary: '#94a3b8',
    },
    success: '#4ade80',
    warning: '#fbbf24',
    error: '#f87171',
  },
  spacing: {
    xs: 4,
    sm: 8,
    md: 16,
    lg: 24,
    xl: 36,
  },
  borderRadius: {
    sm: 6,
    md: 10,
    lg: 18,
    pill: 999,
  },
} as const;

export type Theme = typeof theme;

module.exports = {
  expo: {
    name: 'CRUD Template',
    slug: 'crud-template',
    version: '1.0.0',
    orientation: 'portrait',
    userInterfaceStyle: 'dark',
    newArchEnabled: true,
    splash: {
      resizeMode: 'contain',
      backgroundColor: '#020617',
    },
    ios: {
      supportsTablet: true,
      bundleIdentifier: 'com.yourcompany.crudtemplate',
    },
    android: {
      package: 'com.yourcompany.crudtemplate',
      adaptiveIcon: {
        backgroundColor: '#020617',
      },
    },
    extra: {
      eas: {
        projectId: '',
      },
    },
  },
};
